    icon: Store,
  },
  {
    title: "Agents",
    url: "/agents",
    icon: Cpu,
  },
  {
    title: "Modules",
    url: "/modules",
    icon: Package,
  },
  {
    title: "Execution History",
    url: "/execution-history",
    icon: CheckCircle,
  },
  {
    title: "Settings",
    url: "/settings",
    icon: Settings,
  },
];

export function AppSidebar() {
  const [location] = useLocation();

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.href = "/login";
  };

  const user = {
    name: "Admin User",
    email: "admin@abetworks.com",
    role: "Super Admin",
    initials: "AU",
  };

  return (
    <Sidebar>
      <SidebarContent>
        <div className="flex items-center h-16 px-4 border-b border-sidebar-border">
          <div className="flex items-center gap-3">
            <div className="w-8 h-8 rounded-md bg-primary flex items-center justify-center">
              <span className="text-primary-foreground font-bold text-sm">A</span>
            </div>
            <div>
              <h1 className="font-semibold text-base">Abetworks</h1>
              <p className="text-xs text-muted-foreground">CRM Platform</p>
            </div>
          </div>
        </div>

        <SidebarGroup className="mt-4">
          <SidebarGroupLabel>Navigation</SidebarGroupLabel>
          <SidebarGroupContent>
            <SidebarMenu>
              {menuItems.map((item) => {
                const isActive = location === item.url;
                return (
                  <SidebarMenuItem key={item.title}>
                    <SidebarMenuButton asChild isActive={isActive} data-testid={`link-${item.title.toLowerCase()}`}>
                      <Link href={item.url}>
                        <item.icon className="w-5 h-5" />
