import { useState } from "react";
import { useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { loginSchema, type LoginData } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { Card } from "@/components/ui/card";
import { Loader2, Shield } from "lucide-react";

export default function AdminLogin() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);

  const form = useForm<LoginData>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      email: "",
      password: "",
    },
  });

  const onSubmit = async (data: LoginData) => {
    setIsLoading(true);
    try {
      const response = await fetch("/api/auth/admin/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Admin login failed");
      }

      const result = await response.json();
      localStorage.setItem("token", result.token);

      toast({
        title: result.message || "Welcome back, Admin!",
        description: "You have successfully logged in as administrator.",
      });

      setLocation("/");
    } catch (error: any) {
      toast({
        variant: "destructive",
        title: "Admin login failed",
        description: error.message || "Invalid credentials or insufficient privileges",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-background p-4">
      <Card className="w-full max-w-md p-8">
        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-12 h-12 rounded-md bg-primary mb-4">
            <Shield className="h-6 w-6 text-primary-foreground" />
          </div>
          <h1 className="text-3xl font-semibold tracking-tight mb-2">Admin Portal</h1>
          <p className="text-sm text-muted-foreground">
            Sign in with your administrator account
          </p>
        </div>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="email"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Admin Email</FormLabel>
                  <FormControl>
                    <Input
                      placeholder="admin@abetworks.com"
                      type="email"
                      autoComplete="email"
                      data-testid="input-admin-email"
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="password"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Admin Password</FormLabel>
                  <FormControl>
                    <Input
                      placeholder="Enter your admin password"
                      type="password"
                      autoComplete="current-password"
                      data-testid="input-admin-password"
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <Button
              type="submit"
              className="w-full"
              disabled={isLoading}
              data-testid="button-admin-login"
            >
              {isLoading ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Authenticating...
                </>
              ) : (
                <>
                  <Shield className="mr-2 h-4 w-4" />
                  Admin Sign In
                </>
              )}
            </Button>
          </form>
        </Form>

        <div className="mt-6 text-center space-y-2">
          <p className="text-sm text-muted-foreground">
            Regular user?{" "}
            <a href="/login" className="text-primary hover:underline" data-testid="link-user-login">
              Sign in here
            </a>
          </p>
        </div>
      </Card>
    </div>
  );
}

python-agents/agents/data_agent.py.content:
from .base_agent import BaseAgent
from typing import Any, Dict

class DataAgent(BaseAgent):
    """Data Transformation Agent using pandas"""

    async def execute(self, input_data: Dict[str, Any]) -> Dict[str, Any]:
        """
        Execute data transformation task

        Args:
            input_data: Should contain 'data' key with data to process

        Returns:
            Dict with processed data and statistics
        """
        try:
            data = input_data.get("data", [])
            operation = input_data.get("operation", "describe")

            if not data:
                return {
                    "success": False,
                    "error": "No data provided for processing"
                }

            try:
                import pandas as pd

                df = pd.DataFrame(data)

                if operation == "describe":
                    result = df.describe().to_dict()
                    return {
                        "success": True,
                        "statistics": result,
                        "row_count": len(df),
                        "column_count": len(df.columns),
                        "columns": list(df.columns)
                    }

                elif operation == "summary":
                    return {
                        "success": True,
                        "row_count": len(df),
                        "column_count": len(df.columns),
                        "columns": list(df.columns),
                        "dtypes": df.dtypes.astype(str).to_dict(),
                        "sample": df.head(5).to_dict(orient="records")
                    }

                elif operation == "clean":
                    df_cleaned = df.dropna()
                    return {
                        "success": True,
                        "cleaned_data": df_cleaned.to_dict(orient="records"),
                        "original_rows": len(df),
                        "cleaned_rows": len(df_cleaned),
                        "rows_removed": len(df) - len(df_cleaned)
                    }

                else:
                    return {
                        "success": False,
                        "error": f"Unknown operation: {operation}"
                    }

            except ImportError:
                return {
                    "success": False,
                    "error": "pandas library not installed"
                }

        except Exception as e:
            return {
                "success": False,
                "error": str(e)
            }

